input = ""
testInput = "xBxAAABCDxCC"

def getHealthPotions(input:str):
    sum = 0
    for i in range(0, len(input), 3):
        pair = input[i:i+3]
        if len(pair) < 3:
            continue

        x = pair.count("x")

        if x == 1:
            sum += 2

        if x == 0:
            sum += 6
    
   
        for char in pair:
            if char == "A":
                sum += 0
                continue

            if char == "B":
                sum += 1
                continue

            if char == "C":
                sum += 3
                continue 

            if char == "D":
                sum += 5
                continue

    return sum



print(getHealthPotions(testInput))
print(getHealthPotions(input))
